/* type of font */
.font-inter {
    font-family: "Inter", sans-serif;
    /* Replace 'Inter' with your font's name if different */
}

.font-courier {
    font-family: "Courier", monospace;
}

/* easyjoey logo container */
.logo-container {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 20px;
    /* Adjust the margin as needed */
}

/* Style for the logo */
.logo {
    max-width: 200px;
    /* Set the maximum width of your logo */
    height: auto;
    /* Maintain the aspect ratio */
    margin-right: 10px;
    /* Adjust the margin as needed */
}

/* font animation container */
.custom-container {
    /* width: 100%;
  height: 100vh; */
    display: flex;
    justify-content: center;
    align-items: center;
}

.custom-heading {
    font-size: 3rem;
    font-weight: normal;
}

/* animation blinking */
.custom-heading .typed-text {
    color: #009999;
}

.blinking-cursor {
    font-size: 2.5rem;
    color: #2c3e50;
    -webkit-animation: 1s blink step-end infinite;
    -moz-animation: 1s blink step-end infinite;
    -ms-animation: 1s blink step-end infinite;
    -o-animation: 1s blink step-end infinite;
    animation: 1s blink step-end infinite;
}

@keyframes blink {
    from,
    to {
        color: transparent;
    }

    50% {
        color: #2c3e50;
    }
}

@-moz-keyframes blink {
    from,
    to {
        color: transparent;
    }

    50% {
        color: #2c3e50;
    }
}

@-webkit-keyframes blink {
    from,
    to {
        color: transparent;
    }

    50% {
        color: #2c3e50;
    }
}

@-ms-keyframes blink {
    from,
    to {
        color: transparent;
    }

    50% {
        color: #2c3e50;
    }
}

@-o-keyframes blink {
    from,
    to {
        color: transparent;
    }

    50% {
        color: #2c3e50;
    }
}

/* end of animation blinking */

